import React, { Component } from 'react'
import SignedInLinks from './signButtons/signedIn'
import SignedOutLinks from './signButtons/singneOut'
import { connect } from "react-redux";
import { signIn , signOut } from "../../actions";
import PropTypes from "prop-types";

import './navBarSt.css'


export class Header extends Component {

  constructor(props){
    super()
    this.state = {
      verifLog : false
    }
    this.contextTypes = {
      router: PropTypes.object
    };
  }
  componentWillMount(){
    localStorage.getItem('userSession') && this.setState({
      verifSession : JSON.parse(localStorage.getItem('userSession'))
    })
  }
  loggIn= () => {
    console.log(this.state.verifLog) 
    this.setState({ verifLog: !this.state.verifLog})
    this.contextTypes.router.signIn
  }
  loggOut = () => {
    console.log(this.state.verifLog) 
    this.setState({ verifLog: !this.state.verifLog})
    this.contextTypes.signIn
  }

  render() {
    return (
      this.state.verifLog ? 
        (
          <SignedInLinks
            btnLogOut={this.loggOut}
            // nameUser={this.state.verifSession.name}
            // avatar={this.state.verifSession.avatar}
          />
        ):(
          <SignedOutLinks btnLogIn={this.loggIn} />
        )
    )
  }
}
function mapStateToProps({ auth }) {
  return { auth };
}

export default connect(mapStateToProps, { signIn , signOut })(Header);
