import React, { Component } from 'react'
import { connect } from 'react-redux'
import SignedInLinks from './signButtons/signedIn'
import SignedOutLinks from './signButtons/singneOut'
import { signIn , signOut } from '../../store/actions/actions'
import './navBarSt.css'


export class NavBar extends Component {
  constructor(){
    super()

    this.state = {
      verifLog : false
    }
    this.logInProm  = new Promise ((resolve , reject) =>{
      this.props.signIn()
    })
    logInProm.then(()=>{
      this.setState({ verifLog: !this.state.verifLog})      
    })

  }
  loggIn= async () => {
    await this.setState({ verifLog: !this.state.verifLog})
  }
  loggOut = async () => {
    await this.props.signOut()
    await this.setState({ verifLog: !this.state.verifLog})
  }
  render(props) {  
    return (
      this.state.verifLog ? 
        (
          <SignedInLinks btnLogOut={this.loggOut} />
        ):(
          <SignedOutLinks btnLogIn={this.logInProm } />
        )
    )
  }
}


const mapStateToProps = (state) => {
  return {
    auth: state.firebase.auth
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    signIn: (creds) => dispatch(signIn(creds)),
    signOut: () => dispatch(signOut())
  }
}

export default connect(mapStateToProps , mapDispatchToProps)(NavBar)
