import React, { Component } from 'react'
// CONEXION A FIREBASE
import fireStAuth from '../../config/config'
// import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth'
import logo from '../../assets/logo.svg';
import './header.css'


export class Header extends Component {
  constructor(props){
    super()
    // ESTADO DE CONEXION
    this.signedVerif = false
    this.state = { 
      isSignedIn: false,
      user: {},
      loading: true
    }
    this.authListener = this.authListener.bind(this);
    // // INICIA MAGIA DE FIREBASE
    // this.uiConfig = {
    //   signInFlow: "popup",
    //   signInOptions: [
    //     fireState.auth.GithubAuthProvider.PROVIDER_ID,
    //   ],
    //   callbacks: {
    //     signInSuccess: (currentUser, credential, redirectUrl) => {
    //       // this.state.accessToken = credential.accessToken
    //     },

    //   }
    // }
  }

  authListener(){
    const { navigate } = this.props.navigation;

    fireStAuth.onAuthStateChanged((user) =>{
      console.log(user)
      if(user){
        this.setState({user: user ,  isSignedIn: !!user , loading: false })
        this.props.persistedUserLoginSuccess({user, navigate})
        this.props.fetchUserData();
      }else{
        this.setState({user: null , loading: false})
      }
    })
  }
  componentDidMount(){
    this.authListener()
  }
  render() {

    return (
      <header>
        <div className="logo">
        <img src={logo} alt="Logo" />
        </div>
        <div className="singIn">

        </div>
      </header>
    )
  }
}

export default Header
