import React, { Component } from 'react'
// import { Link } from 'react-router-dom';
// CONEXION A FIREBASE
import firebase from 'firebase'

import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth'
import { AUTH_VERIF , fireState } from '../../config/config'
import logo from '../../assets/logo.svg';
import './header.css'


export class Header extends Component {
  constructor(props){
    super()
    // ESTADO DE CONEXION
    this.signedVerif = false
    this.state = { 
      isSignedIn: false,
      user: {},
    }
    // // INICIA MAGIA DE FIREBASE
    // this.uiConfig = {
    //   signInFlow: "popup",
    //   signInOptions: [
    //     fireState.auth.GithubAuthProvider.PROVIDER_ID,
    //   ],
    //   callbacks: {
    //     signInSuccess: (currentUser, credential, redirectUrl) => {
    //       // this.state.accessToken = credential.accessToken
    //     },

    //   }
    // }
  }

  authListener(){
    fireState.onAuthStateChanged((user) =>{
      console.log(user)
      if(user){
        this.setState({user})
      }else{
        this.setState({user: null})
      }
    })
  }
  // componentDidMount(){
  //   fireState.auth().onAuthStateChanged(user => {
  //     this.setState({ isSignedIn: !!user })
  //     console.log(user)
  //   })    
  // }
  render() {
    return (
      <header>
        <div className="logo">
        <img src={logo} alt="Logo" />
        </div>
        <div className="singIn">
        {this.state.isSignedIn ? (
            <div className="loggedIn">
                <p>Signed In as {firebase.auth().currentUser.displayName}</p>
                <button onClick={() => firebase.auth().signOut()}>Sign out!</button>
            </div>
          ) : (
            <StyledFirebaseAuth
              uiConfig={this.uiConfig}
              firebaseAuth={firebase.auth()}
            />
          )}
        </div>
      </header>
    )
  }
}

export default Header
