import axios from 'axios'


export const fetchAllGists = (name) => {
  console.log(name);
  axios.get(`https://api.github.com/users/${name}/gists`)
  .then(response => {  
    return{
      type: 'FETCH_GISTS',
      payload : response.data  
    }
  })
