const dataReducer = (state = [], action) => {
  console.log(action)
  switch (action.type) {
    case 'FETCH_GISTS':
      console.log('get data')
      return { action }
    case 'DATA_ERROR':
      console.log('error data')
      return state
    default:
      console.log('empty data')
      return state;
  }
};

export default dataReducer;
