import axios from 'axios'


export const fetchAllGists = (name) => {
  return (dispatch) =>{
    axios.get(`https://api.github.com/users/${name}/gists`)
    .then(response => {
      dispatch(fetchGist(response.data))
    })
    .catch(error => {
      throw(error);
    });
  }
};

export const fetchGist = (posts) => {
    return {
      type: 'FETCH_GISTS',
      payload : new Promise((res , rej) =>{
                res(posts)
              }) 
    }
  };
