import React from 'react'
import SignedInLinks from './signButtons/signedIn'
import SignedOutLinks from './signButtons/singneOut'
import { connect } from 'react-redux'
import { signIn , signOut } from '../../store/actions/actions'
import './navBarSt.css'


const Header = (props) => {
  const { auth } = props;
  const links = auth.uid ? <SignedInLinks btnLogOut={props.signOut} /* nameUser={this.state.verifSession.name} // avatar={this.state.verifSession.avatar}*/ /> : <SignedOutLinks btnLogIn={props.signIn} />;
  return ({links})

}

const mapStateToProps = (state) => {
  return{
    auth: state.firebase.auth
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    signIn: (creds) => dispatch(signIn(creds)),
    signOut: () => dispatch(signOut())
  }
}


export default connect(mapStateToProps , mapDispatchToProps)(Header)
