import React, { Component } from 'react'
import firebase from 'firebase'
import { Link } from 'react-router-dom';


import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth'
import {AUTH_VERIF} from '../../config/config'
import logo from '../../assets/logo.svg';
import './header.css'

const frStAuth = firebase;
frStAuth.initializeApp(AUTH_VERIF);

export class Header extends Component {
  constructor(props){
    super()
    // ESTADO DE CONEXION
    this.signedVerif = false
    this.state = { 
      isSignedIn: false,
      user: null 
    }
    // INICIA MAGIA DE FIREBASE
    this.uiConfig = {
      signInFlow: "popup",
      signInSuccessUrl: '/user',
      signInOptions: [
        frStAuth.auth.GithubAuthProvider.PROVIDER_ID,
      ],
      callbacks: {
        signInSuccess: () => true
      }
    }
  }
  componentDidMount(){
    frStAuth.auth().onAuthStateChanged(user => {
      this.setState({ isSignedIn: !!user , user })
    })    
  }
  render() {
    console.log(this.state.user)
    return (
      <header>
        <div className="logo">
          <img src={logo} alt="Logo" />
        </div>
        <div className="singIn">
          <p>Sign in ...</p>
        </div>
      </header>    
    )
  }
}

export default Header
