import React, { Component } from 'react'
import firebase from 'firebase'
import { Link } from 'react-router-dom';


import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth'
import {AUTH_VERIF} from '../../config/config'
import logo from '../../assets/logo.svg';
import './header.css'

const frStAuth = firebase;
frStAuth.initializeApp(AUTH_VERIF);

export class Header extends Component {
  constructor(props){
    super()
    // ESTADO DE CONEXION
    this.signedVerif = false
    this.state = { 
      isSignedIn: false,
      user: {}
    }
    // INICIA MAGIA DE FIREBASE
    this.uiConfig = {
      signInFlow: "popup",
      signInSuccessUrl: '/user',
      signInOptions: [
        frStAuth.auth.GithubAuthProvider.PROVIDER_ID,
      ],
      callbacks: {
        signInSuccess: () => true
      }
    }
  }
  componentDidMount(){
    frStAuth.auth().onAuthStateChanged(user => {
      this.setState({ isSignedIn: !!user , user })
    })    
  }
  render() {
    return (
      <header>
        <div className="logo">
        <img src={logo} alt="Logo" />
        </div>
        <div className="singIn">
        <Link to="/">Home</Link> | <Link to="/user">About</Link>
        {this.state.isSignedIn ? (
            <div className="loggedIn">
                <p>Signed In as {frStAuth.auth().currentUser.displayName}</p>
                <button onClick={() => frStAuth.auth().signOut()}>Sign out!</button>
            </div>
          ) : (
            <StyledFirebaseAuth
            uiConfig={this.uiConfig}
            firebaseAuth={frStAuth.auth()}
            />
          )}
        </div>
      </header>
    )
  }
}

export default Header
