const initState = {
  dataERROR: null, 
}
const dataReducer = (state = initState, action) => {
  switch (action.type) {
    case 'FETCH_GISTS':
      return {...state , dataERROR: null};
    case 'DATA_ERROR':
      return state
    default:
      return state;
  }
};

export default dataReducer;
