const initialState = {
  dataApi: []
}

const dataReducer = (state = initialState , action) => {
  switch (action.type) {
    case 'FETCH_GISTS':
      console.log(action)
      return {... state};
    case 'DATA_ERROR':
      return state
    default:
      return state;
  }
};

export default dataReducer;
