import axios from 'axios'


export const fetchAllGists = (name) => {
  return (dispatch) =>{
    axios.get(`https://api.github.com/users/${name}/gists`)
    .then(response => response.data)
    .then(data =>{
      dispatch(fetchGist(data))
    })
    .catch(error => {
      throw(error);
    });
  }
};

export const fetchGist = (posts) => {
    console.log(posts)
    return {
      type: 'FETCH_GISTS',
      payload : posts
    }
};
