import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import OwnerPosts from '../posts/ownerPosts'
import logo from '../../assets/logo.svg';
import './header.css'


export class Header extends Component {
  constructor(props){
    super()
    this.state = {
      verifSession : {}
    }
  }
  componentWillMount(){
    localStorage.getItem('userSession') && this.setState({
      verifSession : JSON.parse(localStorage.getItem('userSession'))
    })
  }
  render() {
    console.log(this.state.verifSession)
    return (
      <header>
        <Link to="/" className="logo">
          <img src={logo} alt="Logo" />
        </Link>
        { this.state.verifSession ? 
          (
            <OwnerPosts key={this.state.verifSession.lastLogin} description={this.state.verifSession.email} 
              userLogin={this.state.verifSession.name} avatarUrl={this.state.verifSession.avatar} sessionInit={this.state.verifSession.sessionInit}/>
          ):(
            <Link to="/login" className="singIn">
              <p>Sign in ...</p>
            </Link>
          )
        }
      </header>    
    )
  }
}

export default Header
