import { authRef , provider } from "../config/firebase";
import { FETCH_USER } from "./types";

export const fetchUser = () => dispatch => {
  authRef.onAuthStateChanged(user => {
    if (user) {
      dispatch({
        type: FETCH_USER,
        payload: user
      });
    } else {
      dispatch({
        type: FETCH_USER,
        payload: null
      });
    }
  });
};
export const signIn = () => dispatch => {
  authRef
    .signInWithPopup(provider)
    .then(result => {
      console.log(result);
      localStorage.setItem("nombredeusuario", "John");
    })
    .catch(error => {
      console.log(error);
    });
};

export const signOut = () => dispatch => {
  authRef
    .signOut()
    .then(result => {
      console.log(result);
    })
    .catch(error => {
      console.log(error);
    });
};
