import React, { Component } from 'react'

export class InforUser extends Component {
  render() {
    return this.props.user.map(res => (
        <article id={res.login} className="articleRepo">
          {res.login}
          <button>Read more ...</button>
        </article>
    ))
  }
}

export default InforUser
