import React, { Component } from 'react'
import { Link } from 'react-router-dom';


import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth'
import { fireStAuth } from '../../config/config'
import logo from '../../assets/logo.svg';
import './header.css'


export class Header extends Component {
  constructor(props){
    super()
    // ESTADO DE CONEXION
    this.signedVerif = false
    this.state = { isSignedIn: false }
    // INICIA MAGIA DE FIREBASE
    this.uiConfig = {
      signInFlow: "popup",
      signInSuccessUrl: '/user',
      signInOptions: [
        fireStAuth.auth.GithubAuthProvider.PROVIDER_ID,
      ],
      callbacks: {
        signInSuccess: () => false
      }
    }
  }
  componentDidMount(){
    fireStAuth.auth().onAuthStateChanged(user => {
      this.setState({ isSignedIn: !!user })
    })    
  }
  render() {
    return (
      <header>
        <div className="logo">
        <img src={logo} alt="Logo" />
        </div>
        <div className="singIn">
        <Link to="/">Home</Link> | <Link to="/user">About</Link>
        {this.state.isSignedIn ? (
            <div className="loggedIn">
                <p>Signed In as {fireStAuth.auth().currentUser.displayName}</p>
                <button onClick={() => fireStAuth.auth().signOut()}>Sign out!</button>
            </div>
          ) : (
            <StyledFirebaseAuth
            to="/user"
            uiConfig={this.uiConfig}
            firebaseAuth={fireStAuth.auth()}
            />
          )}
        </div>
      </header>
    )
  }
}

export default Header
