import axios from 'axios'

export function loadData(){
    return (dispatch) =>{
        return axios.get("https://api.github.com/users/Tbars84/repos?page=1&per_page=30")
            .then((response)=>{
                dispatch(getData(response.data))
            })
            .catch((err)=>{

            })
    }
}

export function getData(data){
    return{
        type: 'GET_DATA',
        data: data
    }
}
